import React from 'react';
import { css } from '@emotion/css';
import { useNavigate } from 'react-router';

import Card from '../../components/card/Card';
import PrimaryButton from '../../components/button/PrimaryButton';

const productDescriptionCSS = css`
  width: 100%;
  height: 100%;
  text-align: center;
`;

const productNameCSS = css`
  font-weight: 400;
  font-size: 17px;
  margin-top: 14px;
  letter-spacing: 0.5px;
`;

const productPriceCSS = css`
  margin: 14px;
  font-size: 14px;
  font-weight: 400;
  letter-spacing: 0.5px;
`;
const productImageCSS = css`
  img {
    height: 450px;
    width: 100%;
    max-width: 100%;
    object-fit: contain;
  }
  img:hover {
    transform: scale(1.5);
    transition: 0.4s ease-in-out;
 
    overflow: visible;
  }
  &:hover {
    overflow: visible;
  }
  @media (max-width: 700px){
    img:hover{
      transform: scale(1.2);

    }
  }
`;

function ProductCard({ product, onAddtoCart }) {
  const navigate = useNavigate();

  return (
    <Card product={product}>
      <div>
        {!!product.image && (
          <div className={productImageCSS}>
            <img
              src={product.image}
              alt={product.name}
              onClick={() => navigate(`/products/page/${product.id}`, {})}
            />
          </div>
        )}
        <div className={productDescriptionCSS}>
          <h4 className={productNameCSS}>{product.name}</h4>
          <p className={productPriceCSS}>${product.price}</p>
          <PrimaryButton onClick={onAddtoCart} text=" Add to cart" />
        </div>
      </div>
    </Card>
  );
}

export default ProductCard;
